import Map from 'ol/Map';
import OSM from 'ol/source/OSM';
import TileLayer from 'ol/layer/Tile';
import TileWMS from 'ol/source/TileWMS';
import View from 'ol/View';
import LayerSwitcher from 'ol-layerswitcher';


// const layers = [
//     new TileLayer({
//         source: new OSM(),
//     }),
//     new TileLayer({
//         extent: [-13884991, 2870341, -7455066, 6338219],
//         source: new TileWMS({
//             url: 'https://ahocevar.com/geoserver/wms',
//             params: { 'LAYERS': 'topp:states', 'TILED': true },
//             serverType: 'geoserver',
//             // Countries have transparency, so do not fade tiles:
//             transition: 0,
//         }),
//     }),
// ];

export const setInitMap = (view, layer, clickStatus) => {
    let x = document.getElementById('map');
    console.log(x);
    const map = new Map({

        // layers: layers,
        // layers:[
        //     new TileLayer({z
        //         source: new OSM(),
        //       })
        // ],
        target: '',
        view: view ? new View({
            center: [view['lon'], view['lat']],
            zoom: view['zoom'],
            projection: view.projection,
        }) : new View({
            center: [90.41607886403209, 23.82399061856948],
            zoom: 12.70,
            projection: 'EPSG:4326',
        })

    });
    if (clickStatus) {
        map.on('click', (e) => {
            let f=document.getElementById('popup')
            // f.offsetLeft= e.coordinate[1]
            // f.offsetTop= e.coordinate[0]
            // console.log(e);
            console.log(f.offsetLeft);
            //console.log("Latitude ",e.coordinate[0],"  Longititude: ",e.coordinate[1]);
        })
    }

    map.addLayer(new TileLayer({
        source: new OSM(),
    }))
    if (layer) {
        map.addLayer(layer)
    }

    const layerSwitcher = new LayerSwitcher()
    map.addControl(layerSwitcher);
    return map
}
//export default map;
