  },[])
 const clearPrevMap=()=>{
  let x= document.getElementById('map');
  if(x.children>0){
    x.removeChild()
  }
 }
  const show=()=>{
    let x;
        x= setInitMap(view,layer);
     
     
    x.setTarget("map")
  }
  return (
    <div className="map-container">

      <div id='map' className='h-[100vh]'>

      </div>


    </div>
  );
}

export default MapComponent
